import { AppBar, Toolbar, Typography, Modal, Box, TextField, List, Button, IconButton } from "@mui/material";
import { Document } from "react-pdf";
import { Home } from "@mui/icons-material";
import React from "react";
import axios from "axios";
import { DataGrid } from "@mui/x-data-grid";

export default function Main() {

    const [auth, setAuth] = React.useState(false);
    const [openModal, setOpenModal] = React.useState(false);

    const [resumeView, setResumeView] =  React.useState(false);

    let url = null;
    let showResume = (uri) => {setResumeView(true); url = uri};
    let closeResume = () => {setResumeView(false); url = null};

    const loginOpen = () =>  setOpenModal(true);
    const loginClose = () => setOpenModal(false);

    const logIn = () => {setAuth(true)};
    const logOut = () => {
        setAuth(false);
    };

    const [approved, setApproved] = React.useState(null);
    const [pending, setPending] = React.useState(null);

    const getData = () => {
        axios.get("https://node-mongodb-sample-git-rnarveka.apps.cloudapps.unc.edu/resumes").then(res => {
            console.log(res.data);
            let rowsApproved = [];
            let rowsPending = [];
            res.data.forEach(element => {
                if(element.approved === "approved") {
                    rowsApproved.push({
                        id: element._id,
                        name: element.name,
                        major: element.major,
                        resume: element.link,
                        tags: element.tags
                    })
                } else {
                    rowsPending.push({
                        id: element._id,
                        name: element.name,
                        major: element.major,
                        resume: element.link
                    })
                }
            });
            setApproved(rowsApproved);
            setPending(rowsPending);
        })

        let rowsApproved = [];
        let rowsPending = [];
        res.forEach(element => {
            if(element.status === "approved") {
                rowsApproved.push({
                    id: element.id,
                    name: element.name,
                    major: element.major,
                    resume: element.resume,
                    tags: element.tags
                })
            } else {
                rowsPending.push({
                    id: element.id,
                    name: element.name,
                    major: element.major,
                    resume: element.resume
                })
            }
        });
        setApproved(rowsApproved);
        setPending(rowsPending);
    }

    const modalSubmit = () => { 
        getData();
        loginClose(); 
        logIn();
    }

    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 400,
        bgcolor: 'background.paper',
        border: '2px solid #000',
        boxShadow: 24,
        p: 4,
        '& > :not(style)': { m: 1, width: '25ch' }
    };

    const columnsPending = [
        { field: 'id', headerName: 'ID', width: 100 },
        { field: 'name', headerName: 'Name', width: 150 },
        { field: 'major', headerName: 'Major', width: 200 },
        {
            field: 'resume', headerName: 'Resume', width: 275,
            renderCell: (col) => (
                <strong>
                    <Button
                        variant="contained"
                        size="small"
                        style={{ marginLeft: 16 }}
                        href={col.value}>View</Button>
                    <Button
                        variant="contained"
                        size="small"
                        style={{ marginLeft: 16 }}
                        href={col.value}>Accept</Button>
                    <Button
                        variant="contained"
                        size="small"
                        style={{ marginLeft: 16 }}
                        href={col.value}>Reject</Button>
                </strong>
            )
        },
    ];

    const columnsApproved = [
        { field: 'id', headerName: 'ID', width: 100 },
        { field: 'name', headerName: 'Name', width: 150 },
        { field: 'major', headerName: 'Major', width: 200 },
        { field: 'resume', headerName: 'Resume', width: 195,
            renderCell: (col) => (
                <strong>
                    <Button
                        onClick={showResume}
                        variant="contained"
                        size="small"
                        style={{ marginLeft: 16 }}>View</Button>
                    <Button
                        variant="contained"
                        size="small"
                        style={{ marginLeft: 16 }}
                        href={col.value}>Delete</Button>
                </strong>
            )
        },
        { field: 'tags', headerName: 'Tags', width: 400 }
    ];

    return (
        <div>
            <header>
                <AppBar>
                    <Toolbar>
                        <IconButton size="large" edge="start" color="inherit" aria-label="menu" sx={{ mr: 2 }}><Home/></IconButton>
                        <Typography variant="h6" component="div" sx={{ flexGrow : 1}}>
                            Honors Carolina Resume App
                        </Typography>
                        <Button onClick={!auth? loginOpen : logOut} color= "inherit">{auth? "Logout" : "Login"}</Button>
                    </Toolbar>
                </AppBar>
               <h1>Logged In</h1>
                <Modal open={openModal} onClose={loginClose}>
                    <Box sx={style} component="form">
                        <Typography id="modal-modal-title" variant="h6" component="h2">
                            Login
                        </Typography>
                        <TextField id="user" label="Username" variant="outlined" />
                        <TextField id="pass" label="Password" variant="outlined" type="password"/>
                            <Button onClick={modalSubmit} color = "inherit">Login</Button>
                    </Box>
                </Modal>
            </header>
            {auth && <Box m={5} pl={2} pr={2}>
                <Modal open={resumeView} onClose={closeResume}>
                    <Box sx={style} component="form">
                        <Typography id="modal-modal-title" variant="h6" component="h2">
                            Login
                        </Typography>
                        <Document file="https://drive.google.com/file/d/1hezwS7qfRwCvbGV8wNhLqvqXQxQp7-dH/view?usp=sharing"></Document>
                    </Box>
                </Modal>
                <Typography id="modal-modal-title" variant="h6" component="h5">
                    Approved Resumes
                </Typography>
                {approved != null && 
                <div style={{height: 400, width: '100%'}}>
                    <DataGrid
                        rows={approved}
                        columns={columnsApproved}
                        pageSize={5}
                        rowsPerPageOptions={[5]}
                        checkboxSelection
                        pb={2}
                    />
                </div>}
                <Typography id="modal-modal-title" variant="h6" component="h5">
                    Pending Resumes
                </Typography>

                {pending != null && 
                <div style={{height: 400, width: '100%'}}>
                    <DataGrid
                        rows={pending}
                        columns={columnsPending}
                        pageSize={5}
                        rowsPerPageOptions={[5]}
                        checkboxSelection
                        pb={2}
                    />
                </div>}

            </Box>}
        </div>

    )
}
