import React from "react";
import ApprovedResumes from "./ApprovedResume";
import { Box, Typography } from "@mui/material";
import axios from "axios";
const [approved, setApproved] = React.useState(null);
const [pending, setPending] = React.useState(null);

function getData() {
    axios.get("https://node-mongodb-sample-git-rnarveka.apps.cloudapps.unc.edu/resumes").then(res => {
        console.log(res.data);
        let rowsApproved = [];
        let rowsPending = [];
        res.data.forEach(element => {
            if(element.approved === "approved") {
                rowsApproved.push({
                    id: element._id,
                    name: element.name,
                    major: element.major,
                    resume: element.link,
                    tags: element.tags
                })
            } else {
                rowsPending.push({
                    id: element._id,
                    name: element.name,
                    major: element.major,
                    resume: element.link
                })
            }
        });
        setApproved(rowsApproved);
        setPending(rowsPending);
    })
}

export default function ResumesAdmin() {

    return (
        <Box>
            <Box>
                <Typography id="modal-modal-title" variant="h6" component="h5">
                    Approved Resumes
                </Typography>
            </Box>
        </Box>
    )
}
